const rewriteHTML = (html, parentHost, config) => {
  let finalHTML = html;

  // rewrite src="<>" in html to proxy with origin of parent
  finalHTML = finalHTML.replace(/src="(https?:\/\/[^\s"]+)"/g, (match) => {
    const base = match.split("/")[2];
    return match.replace(
      new RegExp(base),
      `${config.rewrite.origin}/${parentHost}/${base}`
    );
  });

  // rewrite href="<>" in html to proxy without origin rewrite
  finalHTML = finalHTML.replace(/href="(https?:\/\/[^\s"]+)"/g, (match) => {
    const base = match.split("/")[2];
    return match.replace(
      base,
      `${base.replace(/\./g, "_")}.${config.rewrite.normal}`
    );
  });

  return finalHTML;
};

module.exports = rewriteHTML;
